package fr.insa.projetIntegrateur.UserService.model;

import java.util.ArrayList;
import java.util.List;

public final class Profile {

	//the ids mean something and arent autogenerated!!
	private static int idProfile;
	private static String profileName;
	
	//fixed profiles values
	//TO MODIFY WITH ACTUAL VALUES!!!!!
	private final List<Float> SPEED = new ArrayList<>();
	private final List<Float> SAFETY = new ArrayList<>();
	private final List<Float> COMFORT = new ArrayList<>();
	
	public Profile() {
	}

	public int getIdProfile() {
		return idProfile;
	}

	public String getProfileName() {
		return profileName;
	}

	//returns the weights for a fixed user profile necessary for the routing computation
	public List<Float> getWeights(int idProfile) {
		switch (idProfile) {
			case 0:
				return SPEED;
			case 1:
				return SAFETY;
			case 2:
				return COMFORT;
			default:
				return new ArrayList<>();
		}
	}
}
